import React, { useState } from 'react';
import moviesData from './movies.json';

function DatabaseManager() {
  const [movies, setMovies] = useState(moviesData);
  const [movieName, setMovieName] = useState('');
  const [editYear, setEditYear] = useState({});
  const [searchQuery, setSearchQuery] = useState('');

  const addMovie = (e) => {
    e.preventDefault();
    const newMovie = { id: movies.length + 1, name: movieName, year: '', comments: [] };
    setMovies([...movies, newMovie]);
    setMovieName(''); // Reset input field
  };

  const deleteMovie = (id) => {
    setMovies(movies.filter(movie => movie.id !== id));
  };

  const updateMovieYear = (id) => {
    const newMovies = movies.map(movie => {
      if (movie.id === id) {
        return { ...movie, year: editYear[id] || movie.year };
      }
      return movie;
    });
    setMovies(newMovies);
  };

  const handleInputChange = (e) => {
    setMovieName(e.target.value);
  };

  const handleYearInputChange = (id, e) => {
    setEditYear({ ...editYear, [id]: e.target.value });
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredMovies = movies.filter(movie => movie.name.toLowerCase().includes(searchQuery.toLowerCase()));

  return (
    <div>
      <h2>Database Manager</h2>
      <form onSubmit={addMovie}>
        <input
          type="text"
          value={movieName}
          onChange={handleInputChange}
          placeholder="Enter movie name"
          required
        />
        <button type="submit">Add Movie</button>
      </form>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search movie"
      />
      <ul>
        {filteredMovies.map(movie => (
          <li key={movie.id}>
            {movie.name} ({movie.year})
            <input
              type="text"
              placeholder="Enter year"
              value={editYear[movie.id] || movie.year}
              onChange={(e) => handleYearInputChange(movie.id, e)}
            />
            <button onClick={() => updateMovieYear(movie.id)}>Update</button>
            <button onClick={() => deleteMovie(movie.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DatabaseManager;
