import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import UserManager from './components/UserManager';
import DatabaseManager from './components/DatabaseManager';



// Example of a dummy movie object
const movies = [
  {
    id: 1,
    name: "Inception",
    comments: [
      { id: 1, text: "Great movie!", user: "User1" },
      { id: 2, text: "A masterpiece!", user: "User2" }
    ]
  }
  // Add more movies as needed
];


function App() {
  return (
    <Router>
      <Routes>
        <Route path="/user" component={UserManager} />
        <Route path="/database-manager" component={DatabaseManager} />
      </Routes>
    </Router>
  );
}

export default App;
