import React, { useState, useEffect } from 'react';
import moviesData from './movies.json';

function DatabaseManager() {
  const [movies, setMovies] = useState(moviesData);
  const [movieName, setMovieName] = useState('');
  const [editYear, setEditYear] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const initialEditYear = moviesData.reduce((acc, movie) => {
    acc[movie.id] = ''; // Initialize with empty string
    return acc;
  }, {});

  const addMovie = (e) => {
    e.preventDefault();
    const newId = movies.length + 1;
    const newMovie = { id: newId, name: movieName, year: '', comments: [] };
    setMovies([...movies, newMovie]);
    setEditYear(prev => ({ ...prev, [newId]: '' })); // Safely update editYear for new movie
    setMovieName('');
  };

  const deleteMovie = (id) => {
    setMovies(movies.filter(movie => movie.id !== id));
    const newEditYear = { ...editYear };
    delete newEditYear[id]; // Remove the entry for the deleted movie
    setEditYear(newEditYear);
  };

  const updateMovieYear = (id) => {
    if (editYear[id]) { // Only update if there's a value in editYear
      const newMovies = movies.map(movie => movie.id === id ? { ...movie, year: editYear[id] } : movie);
      setMovies(newMovies);
      setEditYear({ ...editYear, [id]: '' }); // Reset the input field to empty after update
    }
  };

  const handleInputChange = (e) => {
    setMovieName(e.target.value);
  };

  const handleYearInputChange = (id, e) => {
    setEditYear({ ...editYear, [id]: e.target.value });
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredMovies = movies.filter(movie => movie.name.toLowerCase().includes(searchQuery.toLowerCase()));

  return (
    <div>
      <h2>Database Manager</h2>
      <form onSubmit={addMovie}>
        <input
          type="text"
          value={movieName}
          onChange={handleInputChange}
          placeholder="Enter movie name"
          required
        />
        <button type="submit">Add Movie</button>
      </form>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search movie"
      />
      <ul>
        {filteredMovies.map(movie => (
          <li key={movie.id}>
            {movie.name} ({movie.year})
            <input
              type="text"
              placeholder="Enter year"
              value={editYear[movie.id]}
              onChange={(e) => handleYearInputChange(movie.id, e)}
            />
            <button onClick={() => updateMovieYear(movie.id)}>Update</button>
            <button onClick={() => deleteMovie(movie.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DatabaseManager;
