import React, { useState } from 'react';
import moviesData from './movies.json';

function UserManager() {
  const [movies, setMovies] = useState(moviesData);
  const [commentText, setCommentText] = useState('');
  const [userName, setUserName] = useState(''); // State to store the user's name

  const addComment = (movieId) => {
    const updatedMovies = movies.map(movie => {
      if (movie.id === movieId) {
        const newCommentId = movie.comments.length > 0 ? Math.max(...movie.comments.map(c => c.id)) + 1 : 1;
        const updatedMovie = {
          ...movie,
          comments: [...movie.comments, { id: newCommentId, text: commentText, user: userName }]
        };
        return updatedMovie;
      }
      return movie;
    });
    setMovies(updatedMovies);
    setCommentText(''); // Reset comment input
  };

  const handleCommentChange = (e) => {
    setCommentText(e.target.value);
  };

  const handleUserChange = (e) => {
    setUserName(e.target.value);
  };

  return (
    <div>
      <h2>User Manager</h2>
      <input
        type="text"
        value={userName}
        onChange={handleUserChange}
        placeholder="Your name"
      />
      {movies.map(movie => (
        <div key={movie.id}>
          <h3>{movie.name}</h3>
          <ul>
            {movie.comments.map(comment => (
              <li key={comment.id}>{comment.text} - {comment.user}</li>
            ))}
          </ul>
          <input
            type="text"
            value={commentText}
            onChange={handleCommentChange}
            placeholder="Leave a comment"
          />
          <button onClick={() => addComment(movie.id)}>Add Comment</button>
        </div>
      ))}
    </div>
  );
}

export default UserManager;
